def calculate_floor(input_string):
  going_up = input_string.count("(")
  going_down = input_string.count(")")
  return going_up - going_down

def calculate_basement_step(input_string):
  # Calculate which character/instruction first results in Santa being in the basement.
  position = 0
  for i, char in enumerate(input_string):
    position += 1 if char == "(" else -1
    if position == -1:
    	return i + 1 # (The first instruction is considered #1.)
